variable_def = readFlu(heprowPath + faltwPHSName, delim_whitespace=True, names=['lowE', 'absPHS', 'absSigma'], skiprows=3)
measPHSData = readFlu(heprowPath + measPHSName, delim_whitespace=True, names=['lowE', 'absPHS', 'absSigma'], skiprows=3)
faltwPHSHisto = Histogram()
faltwPHSHisto.build_histo(variable_def['lowE'].tolist(), bin_differentiation(variable_def['lowE'].tolist(), variable_def['absPHS'].tolist()), uncert=variable_def['absSigma'].tolist(), edgeLoc=heprowBinBounds, name='FALTW')
measPHSHisto = Histogram()
measPHSHisto.build_histo(measPHSData['lowE'].tolist(), bin_differentiation(measPHSData['lowE'].tolist(), measPHSData['absPHS'].tolist()), uncert=measPHSData['absSigma'].tolist(), edgeLoc=heprowBinBounds, name='Measured')
faltwPHSHisto.plot(measPHSHisto, logY=True, title='33MeV Deutrons on Ta', xLabel='Light Yield [MeVee]', yLabel='Counts per MeVee')